import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'
import Table from '@material-ui/core/Table'
import TableBody from '@material-ui/core/TableBody'
import TableCell from '@material-ui/core/TableCell'
import TableHead from '@material-ui/core/TableHead'
import TableRow from '@material-ui/core/TableRow'
import Paper from '@material-ui/core/Paper'
import mais from '../images/mais.png'

const rows = []

class listaDeAlunos extends Component {
  constructor() {
    super()
    this.state = {
      alunos: null
    }
  }

  componentDidMount() {
    const config = {
      headers: {
        Authorization: localStorage.FBidToken
      }
    }
    axios
      .get('/listaDeAlunos', config)
      .then(res => {
        for (let aluno in res.data) {
          rows.push(res.data[aluno])
        }
        this.setState({
          alunos: true
        })
      })
      .catch(err => {
        this.setState({
          errors: err.response.data,
          alunos: true
        })
      })
  }

  componentWillUnmount() {
    this.setState({
      alunos: null
    })
  }

  render() {
    return (
      <div>
        <Paper>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell align="center">Nome</TableCell>
                <TableCell align="center">Matr√≠cula</TableCell>
                <TableCell align="center">CPF</TableCell>
                <TableCell align="center">Sexo</TableCell>
                <TableCell align="center">Ver Mais</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {this.state.alunos &&
                rows.map(row => (
                  <TableRow key={row.id}>
                    <TableCell component="th" scope="row" align="center">
                      {row.nome}
                    </TableCell>
                    <TableCell align="center">{row.matricula}</TableCell>
                    <TableCell align="center">{row.cpf}</TableCell>
                    <TableCell align="center">
                      {row.sexo ? 'Masculino' : 'Feminino'}
                    </TableCell>
                    <TableCell>
                      <img
                        alt="ver mais"
                        src={mais}
                        style={{ height: '30px' }}
                      />
                    </TableCell>
                  </TableRow>
                ))}
            </TableBody>
          </Table>
        </Paper>
      </div>
    )
  }
}

export default listaDeAlunos
