import React, { Component } from 'react'
import axios from 'axios'
import MUIDataTable from 'mui-datatables'
import { createBrowserHistory } from 'history'

const history = createBrowserHistory()

const columns = [
  {
    name: 'nome',
    label: 'Nome',
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: 'matricula',
    label: 'Matricula',
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: 'cpf',
    label: 'CPF',
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: 'sexo',
    label: 'Sexo',
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: 'id',
    label: 'Id',
    options: {
      filter: false,
      sort: false,
      display: false
    }
  }
]

const data = []

const options = {
  selectableRows: 'none',
  filter: false,
  print: false,
  download: false,
  viewColumns: false,
  onRowClick: e => {
    history.push({
      pathname: '/aluno',
      state: { id: e[e.length - 1] }
    })
    window.location.reload()
  }
}

class listaDeAlunos extends Component {
  constructor() {
    super()
    this.state = {
      alunos: null
    }
  }

  omponentDidMount() {
    const config = {
      headers: {
        Authorization: localStorage.FBidToken
      }
    }
    axios
      .get('/listaDeAlunos', config)
      .then(res => {
        console.log(res.data)
        this.setState({
          alunos: true
        })
      })
      .catch(err => {
        this.setState({
          errors: err.response.data,
          alunos: true
        })
      })
  }

  componentWillUnmount() {
    this.setState({
      alunos: null
    })
  }

  render() {
    return (
      <MUIDataTable
        title={'Alunos'}
        data={data}
        columns={columns}
        options={options}
      />
    )
  }
}

export default listaDeAlunos
