import React, { Component } from 'react'
import { createBrowserHistory } from 'history'
import { Link } from 'react-router-dom'
import PropTypes from 'prop-types'
import Grid from '@material-ui/core/Grid'
import TextField from '@material-ui/core/TextField'
import MenuItem from '@material-ui/core/MenuItem'
import Button from '@material-ui/core/Button'
import ButtonGroup from '@material-ui/core/ButtonGroup'
import { Typography } from '@material-ui/core'
import withStyles from '@material-ui/core/styles/withStyles'
import Fade from '@material-ui/core/Fade'
import axios from 'axios'
import CircularProgress from '@material-ui/core/CircularProgress'
import InputMask from 'react-input-mask'

const styles = {
  title: {
    textAlign: 'center',
    color: '#333',
    margin: '20px auto 20px auto'
  },
  success: {
    color: 'green',
    fontSize: '1.5rem',
    marginTop: 10,
    marginBottom: 10,
    textAlign: 'center'
  },
  button: {
    position: 'relative'
  },
  progress: {
    position: 'absolute'
  }
}

class aluno extends Component {

  normalizeBoolean = value => {
    if (value === 'Masculino') return true
    if (value === 'Feminino') return false
    return value
  }

  componentDidMount() {
    const history = createBrowserHistory()
    this.setState({
      nome: history.location.state.user[0],
      matricula: history.location.state.user[1],
      cpf: history.location.state.user[2],
      sexo: this.normalizeBoolean(history.location.state.user[3]),
      nascimento: history.location.state.user[4],
      email: history.location.state.user[5],
      telefone: history.location.state.user[6],
      pais: history.location.state.user[7],
    })
  }

  constructor() {
    super()
    this.state = {
      nome: '',
      matricula: '',
      sexo: '',
      cpf: '',
      nascimento: '',
      email: '',
      telefone: '',
      pais: '',
      errors: {},
      msg: {},
      id: null
    }
  }

  handleSubmit = event => {
    event.preventDefault()
    this.setState({
      loading: true
    })
    const config = {
      headers: {
        Authorization: localStorage.FBidToken
      }
    }
    const userData = {
      nome: this.state.nome,
      matricula: this.state.matricula,
      sexo: this.state.sexo === 'Masculino' ? true : false,
      cpf: this.state.cpf.replace(/\./g, '').replace('-', ''),
      nascimento: this.state.nascimento,
      email: this.state.email,
      telefone: this.state.telefone,
      pais: this.state.pais
    }
    axios
      .post('/editarAluno', userData, config)
      .then(res => {
        console.log(res)
        this.setState({
          msg: res.data,
          loading: false
        })
      })
      .catch(err => {
        this.setState({
          errors: err.response.data,
          loading: false
        })
      })
  }

  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  handleReset = () => {
    this.setState({
      nome: '',
      matricula: '',
      sexo: '',
      cpf: '',
      nascimento: '',
      email: '',
      telefone: '',
      pais: ''
    })
  }

  render() {
    const { classes } = this.props
    const { errors, msg, loading } = this.state
    return (
      <Fade in={true} timeout={{ enter: 500 }}>
        <div>
          <Typography variant="h3" className={classes.title}>
            Editar Aluno
          </Typography>
          <form noValidate onSubmit={this.handleSubmit}>
            <Grid container spacing={3}>
              <Grid item xs={12} sm={6}>
                <TextField
                  autoFocus
                  id="nome"
                  name="nome"
                  type="text"
                  label="Nome"
                  helperText={errors.nome}
                  error={errors.nome ? true : false}
                  value={this.state.nome}
                  onChange={this.handleChange}
                  variant="filled"
                  fullWidth
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  id="matricula"
                  name="matricula"
                  type="text"
                  label="Matricula"
                  helperText={errors.matricula}
                  error={errors.matricula ? true : false}
                  value={this.state.matricula}
                  onChange={this.handleChange}
                  variant="filled"
                  fullWidth
                />
              </Grid>
              <Grid item xs={12} sm={4}>
                <TextField
                  select
                  name="sexo"
                  value={this.state.sexo}
                  variant="filled"
                  label="Sexo"
                  helperText={errors.sexo}
                  error={errors.sexo ? true : false}
                  fullWidth
                  onChange={this.handleChange}
                >
                  <MenuItem value={true}>Masculino</MenuItem>
                  <MenuItem value={false}>Feminino</MenuItem>
                </TextField>
              </Grid>
              <Grid item xs={12} sm={4}>
                <InputMask
                  mask="999.999.999-99"
                  value={this.state.cpf}
                  onChange={this.handleChange}
                >
                  {() => (
                    <TextField
                      id="cpf"
                      name="cpf"
                      type="text"
                      label="CPF"
                      helperText={errors.cpf}
                      error={errors.cpf ? true : false}
                      variant="filled"
                      fullWidth
                    />
                  )}
                </InputMask>
              </Grid>
              <Grid item xs={12} sm={4}>
                <InputMask
                  mask="99/99/9999"
                  value={this.state.nascimento}
                  onChange={this.handleChange}
                >
                  {() => (
                    <TextField
                      id="nascimento"
                      name="nascimento"
                      type="text"
                      label="Nascimento"
                      helperText={errors.nascimento}
                      error={errors.nascimento ? true : false}
                      variant="filled"
                      fullWidth
                    />
                  )}
                </InputMask>
              </Grid>
              <Grid item xs={12} sm={4}>
                <TextField
                  id="email"
                  name="email"
                  type="email"
                  label="Email"
                  helperText={errors.email}
                  error={errors.email ? true : false}
                  value={this.state.email}
                  onChange={this.handleChange}
                  variant="filled"
                  fullWidth
                />
              </Grid>
              <Grid item xs={12} sm={4}>
                <InputMask
                  mask="(99) 99999-9999"
                  value={this.state.telefone}
                  onChange={this.handleChange}
                >
                  {() => (
                    <TextField
                      id="telefone"
                      name="telefone"
                      type="text"
                      label="Telefone"
                      helperText={errors.telefone}
                      error={errors.telefone ? true : false}
                      variant="filled"
                      fullWidth
                    />
                  )}
                </InputMask>
              </Grid>
              <Grid item xs={12} sm={4}>
                <TextField
                  id="pais"
                  name="pais"
                  type="text"
                  label="Pais"
                  helperText={errors.pais}
                  error={errors.pais ? true : false}
                  value={this.state.pais}
                  onChange={this.handleChange}
                  variant="filled"
                  fullWidth
                />
              </Grid>
              <Grid item xs={12}>
                {msg.general && (
                  <Typography variant="body2" className={classes.success}>
                    {msg.general}
                  </Typography>
                )}
                <ButtonGroup
                  fullWidth
                  aria-label="Full width contained button group"
                  variant="contained"
                  color="primary"
                >
                  <Button
                    disabled={loading}
                    type="submit"
                    color="dark"
                    className={classes.button}
                  >
                    Confirmar
                    {loading && (
                      <CircularProgress
                        size={30}
                        className={classes.progress}
                      />
                    )}
                  </Button>
                  <Button type="reset" onClick={this.handleReset} color="dark">
                    Limpar
                  </Button>
                  <Button
                    component={Link}
                    to="/listaDeAlunos"
                    type="submit"
                    color="dark"
                  >
                    Voltar
                  </Button>
                </ButtonGroup>
              </Grid>
            </Grid>
          </form>
        </div>
      </Fade>
    )
  }
}

aluno.props = {
  classes: PropTypes.object.isRequired
}

export default withStyles(styles)(aluno)
