import React, { Component } from 'react'
import axios from 'axios'
import MUIDataTable from 'mui-datatables'
import { createBrowserHistory } from 'history'
import CircularProgress from '@material-ui/core/CircularProgress'
import Button from '@material-ui/core/Button'

const history = createBrowserHistory()

const columns = [
  {
    name: 'nome',
    label: 'Nome',
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: 'matricula',
    label: 'Matricula',
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: 'cpf',
    label: 'CPF',
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: 'sexo',
    label: 'Sexo',
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: 'id',
    label: 'Id',
    options: {
      filter: false,
      sort: false,
      display: false
    }
  }
]

const data = []

const options = {
  selectableRows: 'none',
  filter: false,
  print: false,
  download: false,
  viewColumns: false,
  onRowClick: e => {
    history.push({
      pathname: '/aluno',
      state: { id: e[e.length - 1] }
    })
    window.location.reload()
  }
}

class listaDeAlunos extends Component {
  constructor() {
    super()
    this.state = {
      alunos: null,
      loading: true
    }
  }

  componentDidMount() {
    const config = {
      headers: {
        Authorization: localStorage.FBidToken
      }
    }
    axios
      .get('/listaDeAlunos', config)
      .then(res => {
        for (let aluno in res.data) {
          res.data[aluno].sexo = res.data[aluno].sexo ? 'Masculino' : 'Feminino'
          data.push(res.data[aluno])
        }
        this.setState({
          alunos: true,
          loading: false
        })
      })
      .catch(err => {
        this.setState({
          errors: err.response.data,
          alunos: true,
          loading: false
        })
      })
  }

  componentWillUnmount() {
    this.setState({
      alunos: null
    })
  }

  render() {
    return (
      <div>
        {this.state.loading ? (
          <div
            style={{
              display: 'flex',
              height: '80vh',
              justifyContent: 'center',
              alignItems: 'center'
            }}
          >
            <CircularProgress size={100} />
          </div>
        ) : (
          <div>
            <MUIDataTable
              title={'Alunos'}
              data={data}
              columns={columns}
              options={options}
            />
            <Button
              style={{ marginTop: 15, alignSelf: 'center' }}
              variant="contained"
              color="primary"
              onClick={() => history.goBack()}
            >
              Voltar
            </Button>
          </div>
        )}
      </div>
    )
  }
}

export default listaDeAlunos
