import React, { Component } from 'react'
import PropTypes from 'prop-types'
import Grid from '@material-ui/core/Grid'
import TextField from '@material-ui/core/TextField'
import Button from '@material-ui/core/Button'
import ButtonGroup from '@material-ui/core/ButtonGroup'
import { Typography } from '@material-ui/core'
import withStyles from '@material-ui/core/styles/withStyles'
import Fade from '@material-ui/core/Fade'
import axios from 'axios'
import CircularProgress from '@material-ui/core/CircularProgress'
import history from '../util/history'
import formatTitle from '../util/formatTitle'

const styles = {
  title: {
    textAlign: 'center',
    color: '#333',
    margin: '20px auto 20px auto'
  },
  success: {
    color: 'green',
    fontSize: '1.5rem',
    marginTop: 10,
    marginBottom: 10,
    textAlign: 'center'
  },
  button: {
    position: 'relative'
  },
  progress: {
    position: 'absolute'
  }
}

const fields = []

class dadosDesempenho extends Component {
  componentDidMount() {
    this.setState({
      email: history.location.state.email
    })

    const config = {
      headers: {
        Authorization: localStorage.FBidToken
      }
    }

    const user = {
      email: history.location.state.email
    }

    axios
      .post('getDadosDesempenho', user, config)
      .then(res => {
        for (let val in res.data) {
          fields.push(
            <Grid item xs={12} sm={2}>
              <TextField
                name={val}
                type="text"
                label={formatTitle(val)}
                helperText={this.state.errors[val]}
                error={this.state.errors[val] ? true : false}
                value={res.data[val]}
                onChange={this.handleChange}
                variant="filled"
              />
            </Grid>
          )
        }
        console.log(fields)
        this.setState({
          loading: false
        })
      })
      .catch(err => {
        this.setState({
          loading: false
        })
      })
  }

  constructor() {
    super()
    this.state = {
      errors: {},
      msg: {},
      loading: true,
      fields: []
    }
  }

  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  handleReset = () => {
    this.setState({
      nome: '',
      matricula: '',
      sexo: '',
      cpf: '',
      nascimento: '',
      email: '',
      telefone: '',
      pais: ''
    })
  }

  handleSubmit = event => {
    event.preventDefault()
    this.setState({
      loading: true
    })
    const config = {
      headers: {
        Authorization: localStorage.FBidToken
      }
    }
    const userData = {
      nome: this.state.nome,
      matricula: this.state.matricula,
      sexo: this.state.sexo,
      cpf: this.state.cpf.replace(/\./g, '').replace('-', ''),
      nascimento: this.state.nascimento,
      email: this.state.email,
      telefone: this.state.telefone,
      pais: this.state.pais
    }
    axios
      .post('/editarAluno', userData, config)
      .then(res => {
        this.setState({
          msg: res.data,
          loading: false
        })
      })
      .catch(err => {
        console.log(err)
        this.setState({
          errors: err.response.data,
          loading: false
        })
      })
  }

  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  handleReset = () => {
    this.setState({
      nome: '',
      matricula: '',
      sexo: '',
      cpf: '',
      nascimento: '',
      email: '',
      telefone: '',
      pais: ''
    })
  }

  render() {
    const { classes } = this.props
    const { msg, loading } = this.state
    return (
      <Fade in={true} timeout={{ enter: 250 }}>
        <div>
          <Typography variant="h3" className={classes.title}>
            Dados de Desempenho
          </Typography>
          <Typography variant="h5" className={classes.title}>
            {history.location.state.nome}
          </Typography>
          <form noValidate onSubmit={this.handleSubmit}>
            <Grid container spacing={3}>
              {!loading && fields}
              <Grid item xs={12}>
                {msg.message && (
                  <Typography variant="body2" className={classes.success}>
                    {msg.message}
                  </Typography>
                )}
                <ButtonGroup
                  fullWidth
                  aria-label="Full width contained button group"
                  variant="contained"
                  color="primary"
                >
                  <Button
                    disabled={loading}
                    type="submit"
                    color="dark"
                    className={classes.button}
                  >
                    Salvar
                    {loading && (
                      <CircularProgress
                        size={30}
                        className={classes.progress}
                      />
                    )}
                  </Button>
                  <Button type="reset" onClick={this.handleReset} color="dark">
                    Limpar
                  </Button>
                  <Button onClick={() => history.goBack()} color="dark">
                    Voltar
                  </Button>
                </ButtonGroup>
              </Grid>
            </Grid>
          </form>
        </div>
      </Fade>
    )
  }
}

dadosDesempenho.props = {
  classes: PropTypes.object.isRequired
}

export default withStyles(styles)(dadosDesempenho)
