import React, { Component } from 'react'
import IconButton from '@material-ui/core/IconButton'
import Tooltip from '@material-ui/core/Tooltip'
import DeleteIcon from '@material-ui/icons/Delete'
import BlockIcon from '@material-ui/icons/Block'
import { withStyles } from '@material-ui/core/styles'
import axios from 'axios'

const defaultToolbarSelectStyles = {
  iconButton: {
    marginRight: '24px',
    top: '50%',
    display: 'inline-block',
    position: 'relative'
  },
  deleteIcon: {
    color: 'primary'
  }
}

class CustomToolbarSelect extends Component {

  handleDisable = () => {
    const config = {
      headers: {
        Authorization: localStorage.FBidToken
      }
    }

    const id = {
      id: this.props.displayData[
        Number(Object.keys(this.props.selectedRows.lookup))
      ].data[4]
    }

    axios
      .post('/blockAluno', id, config)
      .then(res => {
        console.log(res.data)
      })
      .catch(err => {
        console.log(err)
      })
  }

  handleDelete = () => {
    const config = {
      headers: {
        Authorization: localStorage.FBidToken
      }
    }

    const id = {
      id: this.props.displayData[
        Number(Object.keys(this.props.selectedRows.lookup))
      ].data[4]
    }

    axios
      .post('/deleteAluno', id, config)
      .then(res => {
        console.log(res.data)
      })
      .catch(err => {
        console.log(err)
      })
  }

  render() {
    const { classes } = this.props

    return (
      <div className={'custom-toolbar-select'}>
        <Tooltip title={'Desativar'}>
          <IconButton className={classes.iconButton} onClick={this.handleDisable}>
            <BlockIcon className={classes.deleteIcon} />
          </IconButton>
        </Tooltip>
        <Tooltip title={'Apagar'}>
          <IconButton className={classes.iconButton} onClick={this.handleDelete}>
            <DeleteIcon className={classes.deleteIcon} />
          </IconButton>
        </Tooltip>
      </div>
    )
  }
}

export default withStyles(defaultToolbarSelectStyles, {
  name: 'CustomToolbarSelect'
})(CustomToolbarSelect)
