import React, { Component } from 'react'
import axios from 'axios'
import MUIDataTable from 'mui-datatables'
import CircularProgress from '@material-ui/core/CircularProgress'
import Button from '@material-ui/core/Button'
import history from '../util/history'
import CustomToolbarSelect from '../components/CustomToolbarSelect'

const columns = [
  {
    name: 'nome',
    label: 'Nome',
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: 'matricula',
    label: 'Matricula',
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: 'cpf',
    label: 'CPF',
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: 'sexo',
    label: 'Sexo',
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: 'status',
    label: 'Status',
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: 'id',
    label: 'Id',
    options: {
      filter: true,
      sort: true,
      display: false
    }
  }
]

let data = []

class blockAluno extends Component {
  flushRowsSelected = () => {
    this.setState({ rowsSelected: null }, () =>
      this.setState({ rowsSelected: undefined })
    )
  }

  constructor() {
    super()
    this.state = {
      alunos: null,
      loading: true
    }
  }

  componentDidMount() {
    const config = {
      headers: {
        Authorization: localStorage.FBidToken
      }
    }
    axios
      .get('/listaDeAlunos', config)
      .then(res => {
        console.log(res.data)
        for (let aluno in res.data) {
          res.data[aluno].sexo = res.data[aluno].sexo ? 'Masculino' : 'Feminino'
          res.data[aluno].desativado = res.data[aluno].desativado ? 'Desativado' : 'Ativo'
          data.push(res.data[aluno])
        }
        this.setState({
          alunos: true,
          loading: false
        })
      })
      .catch(err => {
        this.setState({
          errors: err.response.data,
          alunos: true,
          loading: false
        })
      })
  }

  componentWillUnmount() {
    this.setState({
      alunos: null
    })
    data = []
  }

  render() {
    const options = {
      selectableRows: 'single',
      selectableRowsOnClick: true,
      filter: false,
      print: false,
      download: false,
      viewColumns: false,
      textLabels: {
        body: {
          noMatch: 'Nenhum aluno encontrado',
          toolTip: 'Ordenar'
        },
        pagination: {
          next: 'Próxima Página',
          previous: 'Página Anterior',
          rowsPerPage: 'Alunos por página:',
          displayRows: 'de'
        },
        toolbar: {
          search: 'Buscar',
          downloadCsv: 'Baixar Tabela Excel',
          print: 'Imprimir',
          viewColumns: 'Mostrar Colunas',
          filterTable: 'Filtros'
        },
        filter: {
          all: 'Todos',
          title: 'FILTROS',
          reset: 'LIMPAR'
        },
        viewColumns: {
          title: 'Mostrar Colunas',
          titleAria: 'Mostrar/Esconder Colunas'
        },
        selectedRows: {
          text: 'fileira(s) selecionada(s)',
          delete: 'Apagar',
          deleteAria: 'Apagar fileira selecionada'
        }
      },
      customToolbarSelect: (selectedRows, displayData) => (
        <CustomToolbarSelect
          selectedRows={selectedRows}
          flushRowsSelected={this.flushRowsSelected}
          displayData={displayData}
        />
      )
    }

    return (
      <div>
        {this.state.loading ? (
          <div
            style={{
              display: 'flex',
              height: '80vh',
              justifyContent: 'center',
              alignItems: 'center'
            }}
          >
            <CircularProgress size={100} />
          </div>
        ) : (
          <div>
            <MUIDataTable
              title={'Alunos'}
              data={data}
              columns={columns}
              options={options}
            />
            <div style={{ display: 'flex', justifyContent: 'center' }}>
              <Button
                style={{ marginTop: 15 }}
                variant="contained"
                color="primary"
                onClick={() => history.push('/home')}
              >
                Voltar
              </Button>
            </div>
          </div>
        )}
      </div>
    )
  }
}

export default blockAluno
