import React, { Component } from 'react'
import axios from 'axios'
import MUIDataTable from 'mui-datatables'
import CircularProgress from '@material-ui/core/CircularProgress'
import Button from '@material-ui/core/Button'
import history from '../util/history'
import Dialog from '@material-ui/core/Dialog'
import DialogActions from '@material-ui/core/DialogActions'
import DialogContent from '@material-ui/core/DialogContent'
import DialogContentText from '@material-ui/core/DialogContentText'
import DialogTitle from '@material-ui/core/DialogTitle'

const columns = [
  {
    name: 'nome',
    label: 'Nome',
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: 'matricula',
    label: 'Matricula',
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: 'cpf',
    label: 'CPF',
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: 'sexo',
    label: 'Sexo',
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: 'desativado',
    label: 'Status',
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: 'id',
    label: 'Id',
    options: {
      filter: true,
      sort: true,
      display: false
    }
  }
]

let data = []

class blockAluno extends Component {

  constructor() {
    super()
    this.state = {
      alunos: null,
      loading: true
    }
  }

  handleToggle = () => {
    this.setState({
      open: !this.state.open
    })
  }

  componentDidMount() {
    const config = {
      headers: {
        Authorization: localStorage.FBidToken
      }
    }
    axios
      .get('/listaDeAlunos', config)
      .then(res => {
        for (let aluno in res.data) {
          res.data[aluno].sexo = res.data[aluno].sexo ? 'Masculino' : 'Feminino'
          res.data[aluno].desativado = res.data[aluno].desativado
            ? 'Desativado'
            : 'Ativo'
          data.push(res.data[aluno])
        }
        this.setState({
          alunos: true,
          loading: false
        })
      })
      .catch(err => {
        this.setState({
          errors: err.response.data,
          alunos: true,
          loading: false
        })
      })
  }

  componentWillUnmount() {
    this.setState({
      alunos: null
    })
    data = []
  }

  render() {
    const { open } = this.state
    const options = {
      selectableRows: 'none',
      filter: false,
      print: false,
      download: false,
      viewColumns: false,
      onRowClick: event => {
        this.setState({
          open: !this.state.open
        })
        console.log(event.target)
      },
      textLabels: {
        body: {
          noMatch: 'Nenhum aluno encontrado',
          toolTip: 'Ordenar'
        },
        pagination: {
          next: 'Próxima Página',
          previous: 'Página Anterior',
          rowsPerPage: 'Alunos por página:',
          displayRows: 'de'
        },
        toolbar: {
          search: 'Buscar',
          downloadCsv: 'Baixar Tabela Excel',
          print: 'Imprimir',
          viewColumns: 'Mostrar Colunas',
          filterTable: 'Filtros'
        },
        filter: {
          all: 'Todos',
          title: 'FILTROS',
          reset: 'LIMPAR'
        },
        viewColumns: {
          title: 'Mostrar Colunas',
          titleAria: 'Mostrar/Esconder Colunas'
        },
        selectedRows: {
          text: 'fileira(s) selecionada(s)',
          delete: 'Apagar',
          deleteAria: 'Apagar fileira selecionada'
        }
      },
    }

    return (
      <div>
        {this.state.loading ? (
          <div
            style={{
              display: 'flex',
              height: '80vh',
              justifyContent: 'center',
              alignItems: 'center'
            }}
          >
            <CircularProgress size={100} />
          </div>
        ) : (
          <div>
            <MUIDataTable
              title={'Alunos'}
              data={data}
              columns={columns}
              options={options}
            />
            <div style={{ display: 'flex', justifyContent: 'center' }}>
              <Button
                style={{ marginTop: 15 }}
                variant="contained"
                color="primary"
                onClick={() => history.push('/home')}
              >
                Voltar
              </Button>
            </div>
          </div>
        )}

        <Tooltip title={'Ativar/Desativar'}>
          <IconButton
            className={classes.iconButton}
            onClick={this.handleToggle}
          >
            <BlockIcon className={classes.deleteIcon} />
          </IconButton>
        </Tooltip>
        <Tooltip title={'Apagar'}>
          <IconButton
            className={classes.iconButton}
            onClick={this.handleDelete}
          >
            <DeleteIcon className={classes.deleteIcon} />
          </IconButton>
        </Tooltip>

        <Dialog
          open={open}
          onClose={this.handleToggle}
          aria-labelledby="alert-dialog-title"
          aria-describedby="alert-dialog-description"
        >
          <DialogTitle id="alert-dialog-title">
            {'Apagar todos os dados do aluno?'}
          </DialogTitle>
          <DialogContent>
            <DialogContentText id="alert-dialog-description">
              Esta ação não pode ser desfeita.
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={this.handleToggle} color="primary">
              Cancelar
            </Button>
            <Button onClick={this.handleDisable} color="primary" autoFocus>
              Confirmar
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    )
  }
}

export default blockAluno
