import React, { Component } from 'react'
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'
import './App.css'
import MuiThemeProvider from '@material-ui/core/styles/MuiThemeProvider'
import createMuiTheme from '@material-ui/core/styles/createMuiTheme'
import Navbar from './components/Navbar'
import AuthRoute from './util/AuthRoute'
import login, { auth } from './pages/login'
import home from './pages/home'
import listaDeAlunos from './pages/listaDeAlunos'
import addAluno from './pages/addAluno'
import aluno from './pages/aluno'
import jwtDecode from 'jwt-decode'
import dadosDesempenho from './pages/dadosDesempenho';
import history from './util/history'

const theme = createMuiTheme({
  palette: {
    primary: {
      light: '#eeeeee',
      main: '#333',
      dark: '#9e9e9e',
      contrastText: '#fff'
    },
    secondary: {
      light: '#f5f5f5',
      main: '#9e9e9e',
      dark: '#424242',
      contrastText: '#fff'
    }
  },
  typography: {
    useNextVariants: true
  }
})

const token = localStorage.FBidToken
if (token) {
  const decodedToken = jwtDecode(token)
  if (decodedToken.exp * 1000 < Date.now()) {
    window.location.href = '/'
    auth.authenticated = false
  } else {
    auth.authenticated = true
  }
}

class App extends Component {
  render() {
    return (
      <MuiThemeProvider theme={theme}>
        <div className="App">
          <Router  history={history}>
            <Navbar />
            <div className="container">
              <Switch>
                <Route exact path="/" component={login} />
                <AuthRoute
                  exact
                  path="/home"
                  component={home}
                  authenticated={auth.authenticated}
                />
                <AuthRoute
                  exact
                  path="/listaDeAlunos"
                  component={listaDeAlunos}
                  authenticated={auth.authenticated}
                />
                <AuthRoute
                  exact
                  path="/aluno"
                  component={aluno}
                  authenticated={auth.authenticated}
                />
                <AuthRoute
                  exact
                  path="/dadosDesempenho"
                  component={dadosDesempenho}
                  authenticated={auth.authenticated}
                />
                <AuthRoute
                  exact
                  path="/addAluno"
                  component={addAluno}
                  authenticated={auth.authenticated}
                />
              </Switch>
            </div>
          </Router>
        </div>
      </MuiThemeProvider>
    )
  }
}

export default App
