import React, { Component } from 'react'
import IconButton from '@material-ui/core/IconButton'
import Tooltip from '@material-ui/core/Tooltip'
import DeleteIcon from '@material-ui/icons/Delete'
import BlockIcon from '@material-ui/icons/Block'
import { withStyles } from '@material-ui/core/styles'
import axios from 'axios'
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';

const defaultToolbarSelectStyles = {
  iconButton: {
    marginRight: '24px',
    top: '50%',
    display: 'inline-block',
    position: 'relative'
  },
  deleteIcon: {
    color: 'primary'
  }
}

const [open, setOpen] = React.useState(false)

class CustomToolbarSelect extends Component {

  handleClickOpen() {
    setOpen(true)
  }

  handleClose() {
    setOpen(false)
  }

  handleDisable = () => {
    const config = {
      headers: {
        Authorization: localStorage.FBidToken
      }
    }

    const id = {
      id: this.props.displayData[
        Number(Object.keys(this.props.selectedRows.lookup))
      ].data[4]
    }

    axios
      .post('/blockAluno', id, config)
      .then(res => {
        console.log(res.data)
      })
      .catch(err => {
        console.log(err)
      })
  }


  handleDelete = () => {
    const config = {
      headers: {
        Authorization: localStorage.FBidToken
      }
    }

    const id = {
      id: this.props.displayData[
        Number(Object.keys(this.props.selectedRows.lookup))
      ].data[4]
    }

    axios
      .post('/deleteAluno', id, config)
      .then(res => {
        console.log(res.data)
      })
      .catch(err => {
        console.log(err)
      })
  }

  render() {
    const { classes } = this.props

    return (
      <div className={'custom-toolbar-select'}>
        <Tooltip title={'Ativar/Desativar'}>
          <IconButton className={classes.iconButton} onClick={this.handleClickOpen}>
            <BlockIcon className={classes.deleteIcon} />
          </IconButton>
        </Tooltip>
        <Tooltip title={'Apagar'}>
          <IconButton
            className={classes.iconButton}
            onClick={this.handleDelete}
          >
            <DeleteIcon className={classes.deleteIcon} />
          </IconButton>
        </Tooltip>
        <Dialog
          open={open}
          onClose={handleClose}
          aria-labelledby="alert-dialog-title"
          aria-describedby="alert-dialog-description"
        >
          <DialogTitle id="alert-dialog-title">
            {"Use Google's location service?"}
          </DialogTitle>
          <DialogContent>
            <DialogContentText id="alert-dialog-description">
              Let Google help apps determine location. This means sending
              anonymous location data to Google, even when no apps are running.
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose} color="primary">
              Disagree
            </Button>
            <Button onClick={handleClose} color="primary" autoFocus>
              Agree
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    )
  }
}

export default withStyles(defaultToolbarSelectStyles, {
  name: 'CustomToolbarSelect'
})(CustomToolbarSelect)
